import "./style.css";
import React, { useState } from 'react';

function Calculator() {
  const values = [
    "AC",
    "DEL",
    "%",
    "/",
    "7",
    "8",
    "9",
    "X",
    "6",
    "5",
    "4",
    "-",
    "3",
    "2",
    "1",
    "+",
    "0",
    ",",
    "=",
  ];
  const [num, setNum] = useState(0);
  const [firstNum, setFirstNum] = useState(num);

  function percent (){
    setNum(num/100);
  }

  function addCharacter (btn){
    if (num === 0){
      setNum(btn)
    }else{
      if(btn === "%"){
        percent()
      }else{
        setNum(num + btn)   
      }
    }

  }

  function clearCharacter (){
    if(num.length === 1){
      setNum(num.slice(0, -1));
      setNum(0)
    }else{
      setNum(num.slice(0, -1));
    }
  }

  function changeValue (e){
    let btn = e.target.value;

    if (btn === "AC"){
      setNum(0);
    }else{
      if(btn === "DEL"){
        clearCharacter ();
      }else{
        addCharacter (btn);
      }
    }
  }

  const buttons = values.map((value) => (
    <button onClick={changeValue} 
    value={value} 
    id={"btn_" + values.indexOf(value)}>{value}</button>
  ));

  return (
    <div id="content">
      <div id="calculator">
        <div id="display-value">
          <h1>{num}</h1>
        </div>
        <div id="box-calculator">{buttons}</div>
      </div>
    </div>
  );
}

export default Calculator;
