import "./style.css";
import React, { useState } from 'react';

function Calculator() {
  const values = [
    "AC",
    "DEL",
    "%",
    "/",
    "7",
    "8",
    "9",
    "X",
    "6",
    "5",
    "4",
    "-",
    "3",
    "2",
    "1",
    "+",
    "0",
    ",",
    "=",
  ];

  function setNumber (porops){
    const [num, setNum] = useState(0);

    setNum + props.value;
  }

  const buttons = values.map((value) => (
    <button onClick={setNumber} value={value} id={"btn_" + values.indexOf(value)}>{num}</button>
  ));

  return (
    <div id="content">
      <div id="calculator">
        <div id="display-value">
          <h1>0</h1>
        </div>
        <div id="box-calculator">{buttons}</div>
      </div>
    </div>
  );
}

export default Calculator;
